                   current_timestamp);
   END;

   o_status := 'S';
   o_errm := '';
EXCEPTION
   WHEN OTHERS
   THEN
      IF w_status = 'E'
      THEN
         o_status := w_status;
         o_errm := w_errm;
      ELSE
         o_errm := SQLERRM;
         o_status := 'E';
      END IF;
END;
$$;


