-- Name: fn_fin_check_day_month_year(character, date); Type: FUNCTION; Schema: public; Owner: delarship
--

CREATE FUNCTION public.fn_fin_check_day_month_year(p_frequenct character, p_date date) RETURNS boolean
    LANGUAGE plpgsql
    AS $$
DECLARE
   w_month_end         DATE;
   w_quarter_end       DATE;
   w_half_year_end     DATE;
   w_year_end          DATE;
   w_month_start       DATE;
   w_quarter_start     DATE;
   w_half_year_start   DATE;
   w_year_start        DATE;
BEGIN
   SELECT CAST (
               date_trunc ('month', p_date)
             + INTERVAL '1 months'
             - INTERVAL '1 day' AS DATE)
             month_end,
          CAST (
               date_trunc ('quarter', p_date)
             + INTERVAL '3 months'
             - INTERVAL '1 day' AS DATE)
             quarter_end,
          CAST (
               date_trunc ('year', p_date)
             + INTERVAL '6 months'
             - INTERVAL '1 day' AS DATE)
             half_year_end,
          CAST (
               date_trunc ('year', p_date)
             + INTERVAL '12 months'
             - INTERVAL '1 day' AS DATE)
             year_end,
          CAST (date_trunc ('month', p_date) AS DATE)
             month_start,
          CAST (date_trunc ('quarter', p_date) AS DATE)
             quarter_start,
          CAST (
             CAST (EXTRACT (YEAR FROM p_date) AS INTEGER) || '-07-01' AS DATE)
             half_year_start,
          CAST (date_trunc ('year', p_date) AS DATE)
             year_start
     INTO w_month_end,
          w_quarter_end,
          w_half_year_end,
          w_year_end,
          w_month_start,
          w_quarter_start,
          w_half_year_start,
          w_year_start;

   IF p_frequenct = 'D'
   THEN
      RETURN TRUE;
   END IF;

   IF p_frequenct = 'M'
   THEN
      IF w_month_end = p_date
      THEN
         RETURN TRUE;
      END IF;
   END IF;

   IF p_frequenct = 'Q'
   THEN
      IF w_quarter_end = p_date
      THEN
         RETURN TRUE;
      END IF;
   END IF;

   IF p_frequenct = 'H'
   THEN
      IF w_half_year_end = p_date
      THEN
         RETURN TRUE;
      END IF;
   END IF;

   IF p_frequenct = 'Y'
   THEN
      IF w_year_end = p_date
      THEN
         RETURN TRUE;
      END IF;
   END IF;

   RETURN FALSE;
EXCEPTION
   WHEN OTHERS
   THEN
      RETURN FALSE;
END;
$$;


