CREATE FUNCTION public.fn_finance_query_daily_transaction(p_branch_code integer, p_from_date date, p_upto_date date, p_account_number character, p_tran_gl_code character, p_app_user_id character, OUT o_status character, OUT o_errm character) RETURNS record
    LANGUAGE plpgsql
    AS $$
DECLARE
   w_errm        VARCHAR;
   w_status      VARCHAR;
   w_sql_stat    TEXT := '';
   w_ason_date   DATE;
   w_from_date   DATE;
   w_upto_date   DATE;
BEGIN
   IF p_from_date = p_upto_date
   THEN
      w_ason_date := p_from_date;
   ELSE
      w_from_date := p_from_date;
      w_upto_date := p_upto_date;
   END IF;

   DELETE FROM appauth_query_table
         WHERE app_user_id = p_app_user_id;

   IF p_account_number <> '0'
   THEN
      w_sql_stat :=
            'INSERT INTO appauth_query_table (int_column1,
                                  dat_column1,
                                  int_column2,
                                  chr_column1,
                                  chr_column2,
                                  chr_column3,
                                  chr_column4,
                                  chr_column5,
                                  dec_column1,
                                  dec_column2,
                                  chr_column6,
                                  chr_column7,
                                  chr_column8,
                                  dat_column2,
                                  chr_column9,
                                  dat_column3,
                                  app_user_id)
      SELECT t.branch_code,
             t.transaction_date,
             t.batch_number,
             t.tran_gl_code,
             l.gl_name,
             t.account_number,
             a.account_title account_name,
             (case when t.tran_debit_credit=''C'' then ''Payment'' else ''Receipt'' end) tran_debit_credit,
             t.tran_amount,
             t.cancel_amount,
             t.tran_document_number,
             t.transaction_narration,
             t.cancel_by,
             t.cancel_on,
             t.app_user_id,
             t.app_data_time,
             '''
         || p_app_user_id
         || '''
        FROM finance_transaction_details t, finance_general_ledger l, finance_accounts_balance a
       WHERE t.tran_gl_code = l.gl_code 
       and a.account_number=t.account_number
       and a.account_number= '''
         || p_account_number
         || '''';
   ELSIF p_tran_gl_code <> '0'
   THEN
      w_sql_stat :=
            'INSERT INTO appauth_query_table (int_column1,
                                  dat_column1,
                                  int_column2,
                                  chr_column1,
                                  chr_column2,
                                  chr_column3,
                                  chr_column4,
                                  chr_column5,
                                  dec_column1,
                                  dec_column2,
                                  chr_column6,
                                  chr_column7,
                                  chr_column8,
                                  dat_column2,
                                  chr_column9,
                                  dat_column3,
                                  app_user_id)
      SELECT t.branch_code,
             t.transaction_date,
             t.batch_number,
             t.tran_gl_code,
             l.gl_name,
             t.account_number,
             '' '' account_name,
             (case when t.tran_debit_credit=''C'' then ''Payment'' else ''Receipt'' end) tran_debit_credit,
             t.tran_amount,
             t.cancel_amount,
             t.tran_document_number,
             t.transaction_narration,
             t.cancel_by,
             t.cancel_on,
             t.app_user_id,
             t.app_data_time,
             '''
         || p_app_user_id
         || '''
        FROM finance_transaction_details t, finance_general_ledger l
       WHERE t.tran_gl_code = l.gl_code ';
   END IF;



   IF p_account_number <> '0' OR p_tran_gl_code <> '0'
   THEN
      IF p_tran_gl_code <> '0'
      THEN
         w_sql_stat :=
               w_sql_stat
            || ' and t.tran_gl_code = '''
            || p_tran_gl_code
            || '''';
      END IF;

      IF w_from_date IS NOT NULL AND w_upto_date IS NOT NULL
      THEN
         w_sql_stat :=
               w_sql_stat
            || ' and t.transaction_date between '''
            || w_from_date
            || ''' and '''
            || w_upto_date
            || '''';
      END IF;

      IF w_ason_date IS NOT NULL
      THEN
         w_sql_stat :=
               w_sql_stat
            || ' and t.transaction_date = '''
            || w_ason_date
            || '''';
      END IF;

      IF p_branch_code IS NOT NULL
      THEN
         w_sql_stat := w_sql_stat || ' and t.branch_code = ' || p_branch_code;
      END IF;


      --RAISE EXCEPTION USING MESSAGE = w_sql_stat;

      EXECUTE w_sql_stat;
   END IF;

   o_status := 'S';
   o_errm := '';
EXCEPTION
   WHEN OTHERS
   THEN
      IF w_status = 'E'
      THEN
         o_status := w_status;
         o_errm := w_errm;
      ELSE
         o_errm := SQLERRM;
         o_status := 'E';
      END IF;
END;
$$;


