      w_serial_no := w_serial_no + 1;
      w_debit_credit := 'C';
      w_contra_gl_code := w_account_ledger_code;
      w_account_number := '0';

      IF w_tran_amount > 0
      THEN
         INSERT INTO finance_transaction_table (branch_code,
                                                center_code,
                                                transaction_date,
                                                batch_serial,
                                                account_number,
                                                tran_gl_code,
                                                contra_gl_code,
                                                tran_debit_credit,
                                                tran_type,
                                                tran_amount,
                                                available_balance,
                                                tran_person_phone,
                                                tran_person_name,
                                                tran_document_prefix,
                                                tran_document_number,
                                                tran_sign_verified,
                                                system_posted_tran,
                                                transaction_narration,
                                                app_user_id,
                                                app_data_time)
              VALUES (p_branch_code,
                      '0',
                      p_return_date,
                      w_serial_no,
                      w_account_number,
                      w_tran_gl_code,
                      w_contra_gl_code,
                      w_debit_credit,
                      'STOCK_RETURN',
                      w_tran_amount,
                      0,
                      w_customer_phone,
                      '',
                      NULL,
                      w_return_voucher,
                      FALSE,
                      TRUE,
                      w_tran_narration,
                      p_app_user_id,
                      current_timestamp);

         w_serial_no := w_serial_no + 1;
         w_debit_credit := 'D';
         w_account_number := w_supplier_account;
         w_tran_gl_code := '0';
         w_contra_gl_code := product_list.product_stock_gl;

         INSERT INTO finance_transaction_table (branch_code,
                                                center_code,
                                                transaction_date,
                                                batch_serial,
                                                account_number,
                                                tran_gl_code,
                                                contra_gl_code,
                                                tran_debit_credit,
                                                tran_type,
                                                tran_amount,
                                                available_balance,
                                                tran_person_phone,
                                                tran_person_name,
                                                tran_document_prefix,
                                                tran_document_number,
                                                tran_sign_verified,
                                                system_posted_tran,
                                                transaction_narration,
                                                app_user_id,
                                                app_data_time)
              VALUES (p_branch_code,
                      '0',
                      p_return_date,
                      w_serial_no,
                      w_account_number,
                      w_tran_gl_code,
                      w_contra_gl_code,
                      w_debit_credit,
                      'STOCK_RETURN',
                      w_tran_amount,
                      0,
                      w_customer_phone,
                      '',
                      NULL,
                      w_return_voucher,
                      FALSE,
                      TRUE,
                      w_tran_narration,
                      p_app_user_id,
                      current_timestamp);
      END IF;

      INSERT INTO sales_stock_return_details (stock_id,
                                              supplier_id,
                                              stock_voucher,
                                              product_id,
                                              tran_type_code,
                                              tran_batch_number,
