                                              return_amount,
                                              return_voucher,
                                              return_reason,
                                              branch_code,
                                              app_user_id,
                                              app_data_time,
                                              stock_date,
                                              return_date)
           VALUES ('',
                   p_supplier_id,
                   w_stock_voucher_number,
                   product_list.product_id,
                   NULL,
                   NULL,
                   product_list.quantity,
                   product_list.total_price,
                   w_return_voucher,
                   p_return_reason,
                   p_branch_code,
                   p_app_user_id,
                   current_timestamp,
                   w_stock_date,
                   p_return_date);

      SELECT inv_balance_upto_date,
             COALESCE (last_stock_return_date, p_return_date) last_stock_return_date
