             total_purchase_amount =
                total_purchase_amount - product_list.total_price,
             total_stock_return =
                total_stock_return + (product_list.quantity),
             last_stock_return_date = w_last_transaction_date,
             inv_balance_upto_date = w_last_balance_update,
             stock_return_amount =
