CREATE FUNCTION public.fn_sales_stock_submit(p_stock_date date, p_branch_code integer, p_app_user_id character, p_supplier_id integer, p_show_room_id integer, p_voucher_number character, p_comments character, OUT o_status character, OUT o_errm character, OUT o_stock_id character) RETURNS record
    LANGUAGE plpgsql
    AS $$
DECLARE
   w_message               VARCHAR;
   w_stock_id              VARCHAR;
   w_total_product_price   NUMERIC (22, 2);
   w_total_product         NUMERIC (22, 2);
   w_discount_amount       NUMERIC (22, 2);
   w_voucher_number        VARCHAR;
   w_status                VARCHAR;
   w_errm                  VARCHAR;
BEGIN
   w_stock_id :=
      fn_get_inventory_number (30015,
                               100,
                               'ST',
                               'Stock Voucher Number',
                               8);

   INSERT INTO sales_stockdetails (stock_id,
                                   supplier_id,
                                   product_id,
                                   purces_price,
                                   total_price,
                                   discount_amount,
                                   status,
                                   quantity,
                                   returned_quantity,
                                   return_date,
                                   stock_date,
                                   comments,
                                   app_user_id,
                                   app_data_time,
                                   branch_code)
      SELECT w_stock_id,
             p_supplier_id,
             t.product_id,
             t.purces_price,
             t.total_price,
             t.discount_amount,
             'W' status,
             t.quantity,
             0  returned_quantity,
             NULL return_date,
             p_stock_date stock_date,
             t.comments,
             t.app_user_id,
             t.app_data_time,
             p_branch_code
        FROM sales_stockdetailstemp t, sales_products p
       WHERE p.product_id = t.product_id AND t.app_user_id = p_app_user_id;

   SELECT sum (t.total_price), sum (t.quantity), sum (t.discount_amount)
     INTO w_total_product_price, w_total_product, w_discount_amount
     FROM sales_stockdetailstemp t, sales_products p
    WHERE p.product_id = t.product_id AND t.app_user_id = p_app_user_id;

   INSERT INTO sales_stockmasterauthq (stock_id,
                                       supplier_id,
                                       stock_date,
                                       voucher_number,
                                       show_room,
                                       status,
                                       tran_type_code,
                                       payment_comments,
                                       total_quantity,
                                       returned_quantity,
                                       returned_amount,
                                       cancel_quantity,
                                       cancel_amount,
                                       total_price,
                                       discount_amount,
                                       total_pay,
                                       due_amount,
                                       comments,
                                       branch_code,
                                       app_user_id,
                                       app_data_time)
        VALUES (w_stock_id,
                p_supplier_id,
                p_stock_date,
                p_voucher_number,
                p_show_room_id,
                'W',
                'CS',
                NULL,
                w_total_product,
                0,
                0,
                0,
                0,
                w_total_product_price,
                w_discount_amount,
                0,
                w_total_product_price,
                p_comments,
                p_branch_code,
                p_app_user_id,
                current_timestamp);

   DELETE FROM sales_stockdetailstemp
         WHERE app_user_id = p_app_user_id;

   o_stock_id := w_stock_id;
   o_status := 'S';
   o_errm := '';
EXCEPTION
   WHEN OTHERS
   THEN
      IF w_status = 'E'
      THEN
         o_status := w_status;
         o_errm := w_errm;
      ELSE
         o_status := 'E';
         o_errm := SQLERRM;
      END IF;
END;
$$;


