CREATE FUNCTION public.fn_sales_stock_tran_table_insert(p_branch_code integer, p_app_user_id character, p_customer_phone character, p_account_number character, p_bill_amount numeric, p_pay_amount numeric, p_bill_payment_gl character, p_transaction_type character, p_payment_type character, p_transaction_date date, p_document_number character, OUT o_status character, OUT o_errm character) RETURNS record
    LANGUAGE plpgsql
    AS $$
DECLARE
   w_error_message         VARCHAR;
   w_debit_credit          VARCHAR;
   w_batch_serial          INTEGER;
   w_tran_amount           NUMERIC (22, 2) := 0;
   w_total_debit_amount    NUMERIC (22, 2) := 0;
   w_total_credit_amount   NUMERIC (22, 2) := 0;
   w_total_order_amount    NUMERIC (22, 2) := 0;
   w_tran_naration         VARCHAR;
   tran_list               RECORD;
   w_tran_gl_code          VARCHAR := '0';
   w_contra_gl_code        VARCHAR := '0';
   w_serial_no             INTEGER := 0;
   w_status                VARCHAR;
   w_errm                  VARCHAR;
BEGIN
   IF p_transaction_type IN ('STOCK', 'ORDER')
   THEN
      w_debit_credit := 'D';
   ELSE
      w_debit_credit := 'C';
   END IF;

   FOR tran_list
      IN (SELECT *
          FROM (  SELECT sum (tran_amount) tran_amount,
                         tran_gl_code,
                         tran_naration
                    FROM (  SELECT (s.total_price - s.discount_amount)
                                      tran_amount,
                                   (CASE
                                       WHEN p_transaction_type = 'STOCK'
                                       THEN
                                          product_stock_gl
                                       WHEN p_transaction_type = 'SALES'
                                       THEN
                                          product_sales_gl
                                       WHEN p_transaction_type = 'ORDER'
                                       THEN
                                          product_order_gl
                                       WHEN p_transaction_type = 'PROFIT'
                                       THEN
                                          product_profit_gl
                                       WHEN p_transaction_type = 'LOSS'
                                       THEN
                                          product_loss_gl
                                    END)
                                      tran_gl_code,
                                   substr (
                                         initcap (p_transaction_type)
                                      || ' for '
                                      || initcap (p.product_name),
                                      0,
                                      100)
                                      tran_naration
                              FROM sales_stockdetails s, sales_products p
                             WHERE     p.product_id = s.product_id
                                   AND s.stock_id = p_document_number
                          ORDER BY s.id) o
                GROUP BY tran_gl_code, tran_naration) s)
   LOOP
      w_tran_gl_code := tran_list.tran_gl_code;
      w_tran_amount := tran_list.tran_amount;
      w_tran_naration := tran_list.tran_naration;
      w_serial_no := w_serial_no + 1;

      IF w_tran_amount > 0
      THEN
         INSERT INTO finance_transaction_table (branch_code,
                                                center_code,
                                                transaction_date,
                                                batch_serial,
                                                account_number,
                                                tran_gl_code,
                                                contra_gl_code,
                                                tran_debit_credit,
                                                tran_type,
                                                tran_amount,
                                                available_balance,
                                                tran_person_phone,
                                                tran_person_name,
                                                tran_document_prefix,
                                                tran_document_number,
                                                tran_sign_verified,
                                                system_posted_tran,
                                                transaction_narration,
                                                app_user_id,
                                                app_data_time)
              VALUES (p_branch_code,
                      '0',
                      p_transaction_date,
                      w_serial_no,
                      '0',
                      w_tran_gl_code,
                      w_contra_gl_code,
                      w_debit_credit,
                      p_payment_type,
                      w_tran_amount,
                      0,
                      p_customer_phone,
                      p_app_user_id,
                      NULL,
                      p_document_number,
                      FALSE,
                      TRUE,
                      w_tran_naration,
                      p_app_user_id,
                      current_timestamp);
      END IF;
   END LOOP;

   SELECT parent_code
     INTO w_contra_gl_code
     FROM finance_general_ledger
    WHERE reporting_gl_code = w_tran_gl_code;

   IF p_bill_amount > 0
   THEN
      BEGIN
         w_debit_credit := 'C';
         w_tran_amount := p_bill_amount;
         w_tran_naration := 'Purchase bill for ' || p_document_number;
         w_serial_no := w_serial_no + 1;

         INSERT INTO finance_transaction_table (branch_code,
                                                center_code,
                                                transaction_date,
                                                batch_serial,
                                                account_number,
                                                tran_gl_code,
                                                contra_gl_code,
                                                tran_debit_credit,
                                                tran_type,
                                                tran_amount,
                                                available_balance,
                                                tran_person_phone,
                                                tran_person_name,
                                                tran_document_prefix,
                                                tran_document_number,
                                                tran_sign_verified,
                                                system_posted_tran,
                                                transaction_narration,
                                                app_user_id,
                                                app_data_time)
              VALUES (p_branch_code,
                      '0',
                      p_transaction_date,
                      w_serial_no,
                      p_account_number,
                      '0',
                      w_contra_gl_code,
                      w_debit_credit,
                      p_payment_type,
                      w_tran_amount,
                      0.00,
                      p_customer_phone,
                      NULL,
                      NULL,
                      p_document_number,
                      FALSE,
                      TRUE,
                      w_tran_naration,
                      p_app_user_id,
                      current_timestamp);
      END;
   END IF;


   IF p_pay_amount > 0 AND p_bill_amount > 0
   THEN
      BEGIN
         w_debit_credit := 'C';
         w_tran_amount := p_pay_amount;
         w_tran_naration := 'Purchase payment for ' || p_document_number;
         w_serial_no := w_serial_no + 1;
         w_tran_gl_code := p_bill_payment_gl;

         INSERT INTO finance_transaction_table (branch_code,
                                                center_code,
                                                transaction_date,
                                                batch_serial,
                                                account_number,
                                                tran_gl_code,
                                                contra_gl_code,
                                                tran_debit_credit,
                                                tran_type,
                                                tran_amount,
                                                available_balance,
                                                tran_person_phone,
                                                tran_person_name,
                                                tran_document_prefix,
                                                tran_document_number,
                                                tran_sign_verified,
                                                system_posted_tran,
                                                transaction_narration,
                                                app_user_id,
                                                app_data_time)
              VALUES (p_branch_code,
                      '0',
                      p_transaction_date,
                      w_serial_no,
                      '0',
                      w_tran_gl_code,
                      w_contra_gl_code,
                      w_debit_credit,
                      p_payment_type,
                      w_tran_amount,
                      0.00,
                      p_customer_phone,
                      NULL,
                      NULL,
                      p_document_number,
                      FALSE,
                      TRUE,
                      w_tran_naration,
                      p_app_user_id,
                      current_timestamp);
      END;
   END IF;


   IF p_pay_amount > 0 AND p_bill_amount > 0
   THEN
      BEGIN
         w_debit_credit := 'D';
         w_tran_amount := p_pay_amount;
         w_tran_naration := 'Purchase Payment for ' || p_document_number;
         w_serial_no = w_serial_no + 1;

         INSERT INTO finance_transaction_table (branch_code,
                                                center_code,
                                                transaction_date,
                                                batch_serial,
                                                account_number,
                                                tran_gl_code,
                                                contra_gl_code,
                                                tran_debit_credit,
                                                tran_type,
                                                tran_amount,
                                                available_balance,
                                                tran_person_phone,
                                                tran_person_name,
                                                tran_document_prefix,
                                                tran_document_number,
                                                tran_sign_verified,
                                                system_posted_tran,
                                                transaction_narration,
                                                app_user_id,
                                                app_data_time)
              VALUES (p_branch_code,
                      '0',
                      p_transaction_date,
                      w_serial_no,
                      p_account_number,
                      '0',
                      w_contra_gl_code,
                      w_debit_credit,
                      p_payment_type,
                      w_tran_amount,
                      0.00,
                      p_customer_phone,
                      NULL,
                      NULL,
                      p_document_number,
                      FALSE,
                      TRUE,
                      w_tran_naration,
                      p_app_user_id,
                      current_timestamp);
      END;
   END IF;

   o_status := 'S';
   o_errm := '';
EXCEPTION
   WHEN OTHERS
   THEN
      IF w_status = 'E'
      THEN
         o_status := w_status;
         o_errm := w_errm;
      ELSE
         o_status := 'E';
         o_errm := SQLERRM;
      END IF;
END;
$$;


