-- Name: fn_sales_target_submit(integer, character); Type: FUNCTION; Schema: public; Owner: delarship
--

CREATE FUNCTION public.fn_sales_target_submit(p_branch_code integer, p_app_user_id character, OUT o_status character, OUT o_errm character, OUT o_stock_id character) RETURNS record
    LANGUAGE plpgsql
    AS $$
DECLARE
   w_message               VARCHAR;
   w_stock_id              VARCHAR;
   w_total_product_price   NUMERIC (22, 2);
   w_total_product         NUMERIC (22, 2);
   w_unit_price            NUMERIC (22, 2);
   w_status                VARCHAR;
   w_errm                  VARCHAR;
BEGIN
   w_stock_id :=
      fn_get_inventory_number (30015,
                               100,
                               'ST',
                               'Stock Voucher Number',
                               8);

   INSERT INTO delar_sr_target_details (transaction_id,
                                        branch_code,
                                        product_id,
                                        unit_id,
                                        total_quantity,
                                        unit_price,
                                        total_price,
                                        is_deleted,
                                        app_user_id,
                                        app_data_time)
      SELECT w_stock_id,
             p_branch_code,
             t.product_id,
             t.unit_id,
             t.total_quantity,
             t.unit_price,
             t.total_price,
             TRUE delated,
             t.app_user_id,
             t.app_data_time
        FROM delar_sr_target_details_temp t, sales_products p
       WHERE p.product_id = t.product_id AND t.app_user_id = p_app_user_id;

   SELECT sum (t.total_price), sum (t.total_quantity), sum (t.unit_price)
     INTO w_total_product_price, w_total_product, w_unit_price
     FROM delar_sr_target_details_temp t, sales_products p
    WHERE p.product_id = t.product_id AND t.app_user_id = p_app_user_id;



   DELETE FROM delar_sr_target_details_temp
         WHERE app_user_id = p_app_user_id;

   o_stock_id := w_stock_id;
   o_status := 'S';
   o_errm := '';
EXCEPTION
   WHEN OTHERS
   THEN
      IF w_status = 'E'
      THEN
         o_status := w_status;
         o_errm := w_errm;
      ELSE
         o_status := 'E';
         o_errm := SQLERRM;
      END IF;
END;
$$;


